import React, { useState } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  Alert,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from "@mui/material";

function App() {
  const [url, setUrl] = useState("");
  const [shortUrls, setShortUrls] = useState([]);
  const [error, setError] = useState("");

  // Validate URL
  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  // Handle Shorten
  const handleShorten = () => {
    if (!url) {
      setError("Please enter a URL");
      return;
    }
    if (!isValidUrl(url)) {
      setError("Invalid URL format");
      return;
    }

    setError("");

    // Generate short code
    const shortCode = Math.random().toString(36).substring(2, 8);

    const newShortUrl = {
      original: url,
      shortCode: shortCode, // display only
      clicks: 0,
    };

    setShortUrls([...shortUrls, newShortUrl]);
    setUrl("");
  };

  // Simulate click count update
  const handleVisit = (index) => {
    const updated = [...shortUrls];
    updated[index].clicks += 1;
    setShortUrls(updated);
    window.open(updated[index].original, "_blank");
  };

  return (
    <Container maxWidth="md" sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom align="center">
        ðŸš€ URL Shortener
      </Typography>

      <Card sx={{ p: 2, mb: 3 }}>
        <CardContent>
          <TextField
            fullWidth
            label="Enter a long URL"
            variant="outlined"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          {error && (
            <Alert severity="error" sx={{ mt: 2 }}>
              {error}
            </Alert>
          )}
          <Button
            variant="contained"
            color="primary"
            sx={{ mt: 2 }}
            onClick={handleShorten}
          >
            Shorten URL
          </Button>
        </CardContent>
      </Card>

      {shortUrls.length > 0 && (
        <Card sx={{ p: 2 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              ðŸ“Š Shortened URLs & Statistics
            </Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Original URL</TableCell>
                  <TableCell>Short URL</TableCell>
                  <TableCell>Clicks</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {shortUrls.map((item, index) => (
                  <TableRow key={index}>
                    <TableCell>{item.original}</TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        color="secondary"
                        onClick={() => handleVisit(index)}
                      >
                        {`short.ly/${item.shortCode}`}
                      </Button>
                    </TableCell>
                    <TableCell>{item.clicks}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      )}
    </Container>
  );
}

export default App;
